mapper.insert:

insert into fuwu (fwid, bwid, fwflid, 
  fwcoding, fwname, duration, 
  fwjia, dfbeizhu, fwstart
  )
values (#{fwid,jdbcType=INTEGER}, #{bwid,jdbcType=INTEGER}, #{fwflid,jdbcType=INTEGER}, 
  #{fwcoding,jdbcType=VARCHAR}, #{fwname,jdbcType=VARCHAR}, #{duration,jdbcType=INTEGER}, 
  #{fwjia,jdbcType=REAL}, #{dfbeizhu,jdbcType=VARCHAR}, #{fwstart,jdbcType=VARCHAR}
  )

mapper.select:

select fwid, bwid, fwflid, fwcoding, fwname, duration, fwjia, dfbeizhu, fwstart
from fuwu
where fwid = #{fwid,jdbcType=INTEGER}
