mapper.insert:

insert into ypxs (xsid, empno, ckid, 
  cusid, xsdh, xstate, 
  xsnum, cksta, xsdate
  )
values (#{xsid,jdbcType=INTEGER}, #{empno,jdbcType=INTEGER}, #{ckid,jdbcType=INTEGER}, 
  #{cusid,jdbcType=INTEGER}, #{xsdh,jdbcType=VARCHAR}, #{xstate,jdbcType=VARCHAR}, 
  #{xsnum,jdbcType=INTEGER}, #{cksta,jdbcType=VARCHAR}, #{xsdate,jdbcType=TIMESTAMP}
  )

mapper.select:

select xsid, empno, ckid, cusid, xsdh, xstate, xsnum, cksta, xsdate
from ypxs
where xsid = #{xsid,jdbcType=INTEGER}
